insert.query:
make_update_query("tbl_gastos",array(
            "liquidado"=>execute_query("SELECT CASE COUNT(*) WHEN 0 THEN '".intval(getParam("liquidado"))."' ELSE CASE COUNT(*)-SUM(pagado) WHEN 0 THEN 1 ELSE 0 END END liquidado FROM tbl_gastos_v WHERE id_gasto='".getParam("id")."'")
        ),make_where_query(array(
            "id"=>intval(getParam("id"))
        )))

