insert.query:
make_update_query("tbl_facturas",array(),make_where_query(array(
			"id"=>intval(getParam("id"))
		)),array(
			"cobrado"=>"SELECT CASE COUNT(*) WHEN 0 THEN '".intval(getParam("cobrado"))."' ELSE CASE COUNT(*)-SUM(cobrado) WHEN 0 THEN 1 ELSE 0 END END cobrado FROM tbl_facturas_v WHERE id_factura='".intval(getParam("id"))."'"
		))

update.query:
make_update_query("tbl_facturas",array(),make_where_query(array(
			"id"=>intval(getParam("id"))
		)),array(
			"cobrado"=>"SELECT CASE COUNT(*) WHEN 0 THEN '".intval(getParam("cobrado"))."' ELSE CASE COUNT(*)-SUM(cobrado) WHEN 0 THEN 1 ELSE 0 END END cobrado FROM tbl_facturas_v WHERE id_factura='".intval(getParam("id"))."'"
		))
