update.query:
getParam("password_new")?make_update_query("tbl_usuarios",array(
			"password"=>hash_password(getParam("password_new"))
		),make_where_query(array(
			"id"=>current_user()
		))):""

getParam("password_new")?make_update_query("tbl_security",array(
			"logout"=>1
		),make_where_query(array(
			"id_usuario"=>current_user()
		))):""

execute_query("SELECT COUNT(*) FROM tbl_gcalendar WHERE id_usuario='".current_user()."'")?make_update_query("tbl_gcalendar",array(
			"email"=>getParam("email"),
			"token"=>getParam("token")
		),make_where_query(array(
			"id_usuario"=>current_user()
		))):make_insert_query("tbl_gcalendar",array(
			"id_usuario"=>current_user(),
			"email"=>getParam("email"),
			"token"=>getParam("token")
		))

getParam("feeds_del")?"DELETE FROM tbl_usuarios_f WHERE ".make_where_query(array(
			"id"=>intval(getParam("id")),
			"id_usuario"=>current_user()
		)):make_update_query("tbl_usuarios_f",array(
			"disabled"=>intval(getParam("disabled"))
		),make_where_query(array(
			"id"=>intval(getParam("id")),
			"id_usuario"=>current_user()
		)))
